import "./Home.css";
import "bootstrap/dist/css/bootstrap.min.css";
import NavBar from "../../components/Navbar/Navbar";
import { Container, Col, Row, Button } from "react-bootstrap";
import { useState, useEffect } from "react";
// import { useSelector } from "react-redux";
// import { updateTimes } from "../../stores/Time";
function Home() {
  // const dispatch = useDispatch();
  // const times = useSelector((s) => s.time.times);
  const [time, setTime] = useState("");
  useEffect(() => {
    const interval = setInterval(() => {
      var today = new Date(),
        times =
          today.getHours() +
          ":" +
          (today.getMinutes() < 10 ? "0" : "") +
          today.getMinutes() +
          ":" +
          today.getSeconds();
      setTime(times);
    }, 1000);
    return () => clearInterval(interval);
  });
  return (
    <div className="Home">
      <NavBar />
      <Container
        fluid
        className="jumbotron"
        style={{
          backgroundImage: `url("/images/bg-img.png")`,
        }}
      >
        <div className="jumbotron-content">
          <Row>
            <Col md={5}>
              <img
                className="avatar img-responsive "
                src="/images/avatar.jpg"
                alt="avatar img-responsive"
              />
            </Col>
            <Col className="kanan" md={6}>
              <h4 className="hi">Hi, My name is</h4>
              <h1 className="name">Anne Sullivan</h1>
              <h3 className="desc">I build things for the web</h3>
              <Button variant="warning" id="button-utama">
                Get In Touch
              </Button>{" "}
            </Col>
            <Col md={1}>{time}</Col>
          </Row>
        </div>
        {time}
      </Container>
      <link rel="stylesheet" href="%PUBLIC_URL%/css/Home.css" type="text/css" />
    </div>
  );
}

export default Home;
