import "./News.css";
import { Container, Row, Col } from "react-bootstrap";
import NavBar from "../../components/Navbar/Navbar";
import CardNews from "../../components/Card/CardNews";
import { useEffect, useState } from "react";
import axios from "axios";
function News() {
  const [news, setNews] = useState([]);
  useEffect(() => {
    const getPokemon = async () => {
      const response = await axios(
        "https://newsapi.org/v2/top-headlines?country=id&category=technology&apiKey=160115ded7b948d29103ec8a464f79b8"
      );
      const res = await response.data.articles;
      console.log(res);
      setNews(res);
    };
    getPokemon();
  }, []);
  return (
    <>
      <NavBar />
      <Container fluid style={{ backgroundImage: url }} className="cover">
        <Row className="justify-content-md-center">
          <Col md={9}>
            <h1 className="h1">{news[0].title}</h1>
          </Col>
        </Row>
      </Container>
      <Container>
        <Row>
          {news?.slice(1).map((v) => {
            return (
              <Col md={3} style={{ marginTop: "20px" }}>
                {" "}
                <CardNews
                  title={v.title}
                  description={v.description}
                  image={v.urlToImage}
                  link={v.url}
                  source={v.source.name}
                />
              </Col>
            );
          })}
        </Row>
      </Container>
    </>
  );
}
export default News;
