import { createSlice } from "@reduxjs/toolkit";

export const timeSlice = createSlice({
  name: "time",
  initialState: {
    times: 0,
  },
  reducers: {
    updateTimes: (state, action) => {
      var today = new Date(),
        time =
          today.getHours() +
          ":" +
          today.getMinutes() +
          ":" +
          today.getSeconds();
      state.times = time;
    },
  },
});

export const { updateTimes } = timeSlice.actions;
export default timeSlice.reducer;
